nta.declaration:
/**
 * Constants
 */

const int SLOTS = 110;
const int STATIONS = 6;
const int IN_SENSORS = 6; 
const int OUT_SENSORS = 5;

typedef int[0, SLOTS - 1] SlotId;
typedef int[0, STATIONS - 1] StationId;
typedef int[0, IN_SENSORS - 1] InSensorId;
typedef int[0, OUT_SENSORS - 1] OutSensorId;

const SlotId CONTROLLER_POS = 64;
const SlotId BRANCH_FIRST_POS = 86;
const SlotId MERGE_POS = 103;
const SlotId POS_IN_SENSORS[IN_SENSORS] = {13, 34, 64, 77, 93, 107};
const SlotId POS_STATIONS[STATIONS] = {17, 38, 81, 88, 97, 109};
const InSensorId IN_SENSOR_OF_OUT_SENSOR[OUT_SENSORS] = {0, 1, 3, 4, 5};

/**
 * Channels
 */

broadcast chan tick, initialize, station_free[STATIONS], station_busy[STATIONS];
broadcast chan switch_branch;

/**
 * Variables
 */

bool slot_busy[SLOTS];              // true = busy; false = free;
bool in_sensors_state[IN_SENSORS];     // true = locked; false = unlocked;
bool out_sensors_state[OUT_SENSORS];   // true = queue is full; false = can send piece;
bool updateSensor = true;

/**
 * Verification
 */

bool check_queue(const OutSensorId id, const SlotId position) {
    int i;
    for (i = position - 1; i <= POS_IN_SENSORS[IN_SENSOR_OF_OUT_SENSOR[id]]; i++)
        if (!slot_busy[i])
            return true;
    return false;
}

template.declaration:
clock x;

/**    Set the entire System on the initial configuration    **/
void init_system() {
    SlotId i = POS_IN_SENSORS[0];
    for(d : int[0, n_disks-1]) {
        slot_busy[i-d] = true;
    }
}

clock x;
SlotId first_proc_slot;

const double PI = 3.14;
int stochasticProcessingTime;

/**    Set the Station on the initial configuration    **/
void init_station() {
    if(in_sensor_slot == CONTROLLER_POS)
        first_proc_slot = BRANCH_FIRST_POS;
    else
        first_proc_slot = in_sensor_slot + 1;
}

/**    Produce a disk and place it on the next slot    **/
void complete_elaboration() {
    slot_busy[slot_id] = false;
    if (slot_id == SLOTS-1)
        slot_busy[0] = true;
    else
        slot_busy[slot_id + 1] = true;
}

/**    Check whether the next slot is free to receive a disk or not    **/
bool next_slot_is_free() {
    if (slot_id == SLOTS-1) {
        if (!slot_busy[0])
            return true;
    }
    else {
        if (!slot_busy[slot_id+1])
            return true;
    }
    return false;
}

/**    Value generator according to a Normal distribution    **/
double stdNormal(){
    return sqrt(-2 * ln(1 - random(1))) * cos(2*PI * random (1));
}

double Normal (double mean, double stdDev) {
    return mean + stdDev * stdNormal();
}

int f() {
    double var = Normal (elaboration_time, stdDeviation);
    if (var <= 0) {
        var = 1;
    }

    return fint(var);
}

bool take_branch;

/**    Get the id of the In-Sensor at position "pos"    **/
InSensorId get_sensor_id(SlotId pos) {
    for(i : InSensorId)
        if(POS_IN_SENSORS[i] == pos)
            return i;
    return 0;
}

/**    Get the slot type at position "slot" (0 = normal slot; 1 = station; 2 = In-Sensor)    **/
int[0, 2] get_slot_type(const SlotId slot) {
    for(i : StationId)
        if(slot == POS_STATIONS[i])
            return 1;

    for(i : InSensorId)
        if(slot == POS_IN_SENSORS[i])
            return 2;

    return 0;
}

/**    Move the disk from position "prev_slot" to position "curr_slot", if possible    **/
void try_move(SlotId old_pos, SlotId new_pos) {
    if(slot_busy[old_pos] && !slot_busy[new_pos]) {
        slot_busy[old_pos] = false;
        slot_busy[new_pos] = true;
    } 
}

/**    Handle the two slots where the two alternative branches begin (positions 65 and 86)    **/
void manage_branch() {
    if(!in_sensors_state[2]) {
        if(take_branch)
            try_move(CONTROLLER_POS, BRANCH_FIRST_POS);
        else
            try_move(CONTROLLER_POS, CONTROLLER_POS+1);
    }
}

/**    Move the entire Conveyor Belt, slot by slot (by decreasing id) and, where possible, shift the disks    **/
void update(){
    int[0, SLOTS] i = SLOTS;
    while(i > 0) {
        i--;
        if(slot_busy[i] && get_slot_type(i) != 1) {
            if(i == CONTROLLER_POS){
                manage_branch();
            }
            else {
                if(i == BRANCH_FIRST_POS-1){
                    try_move(i, MERGE_POS);
                }
                else {
                    if(get_slot_type(i) == 2) {
                        if(!in_sensors_state[get_sensor_id(i)])
                            try_move(i, i+1);
                    } 
                    else { 
                        try_move(i, i+1);
                    }
                }           
            }
        }
    }
}

bool queue_2_full() {
    int i = POS_IN_SENSORS[3];
    while (i >= pos_out_sensor_2) {
        if (!slot_busy[i]) return false;
        i--;
    }
    return true;
}

bool queue_4_full() {
    int i = POS_IN_SENSORS[4];
    while (i >= pos_out_sensor_3) {
        if (!slot_busy[i]) return false;
        i--;
    }
    return true;
}

nta.system:
/**********************************************************************************/ /*** CONFIGURATION: Change here the Configuration Parameters of the plant: ***/ /**********************************************************************************/ /*** Speed of the Conveyor Belt (Slots per Unit of Time): ***/ const int SPEED = /***/ 1 ;/***/ /*** Number of Disks: ***/ const int[1, 12] DISKS = /***/ 12 ;/***/ /***/ /***/ /*** Positions of the 5 Out-Sensors: ***/ const SlotId POS_OUT_SENSORS[OUT_SENSORS] = { /***/ 2 /** ID: 0 (1st) */ ,/***/ /***/ 24 /** ID: 1 (2nd) */ ,/***/ /***/ 68 /** ID: 2 (3rd) */ ,/***/ /***/ 90 /** ID: 3 (4th) */ ,/***/ /***/ 105 /** ID: 4 (5th) */ };/***/ /***/ /***/ /*** Elaboration Times of the 6 Stations: ***/ const int STATIONS_ELABORATION_TIME[STATIONS] = { /***/ 1 /** ID: 0 (1st)*/ ,/***/ /***/ 1 /** ID: 1 (2nd)*/ ,/***/ /***/ 1 /** ID: 2 (3rd)*/ ,/***/ /***/ 1 /** ID: 3 (4th)*/ ,/***/ /***/ 1 /** ID: 4 (5th)*/ ,/***/ /***/ 1 /** ID: 5 (6nd)*/ };/***/ /***/ /***/ /*** Standard Deviation of the Elaboration Time of the 6 Stations: ***/ const double STD_DEV_STATIONS[STATIONS] = { /***/ 1.0 /** ID: 0 (1st)*/ ,/***/ /***/ 1.0 /** ID: 1 (2nd)*/ ,/***/ /***/ 1.0 /** ID: 2 (3rd)*/ ,/***/ /***/ 1.0 /** ID: 3 (4th)*/ ,/***/ /***/ 1.0 /** ID: 4 (5th)*/ ,/***/ /***/ 1.0 /** ID: 5 (6nd)*/ };/***/ /***/ /***/ /*** Probabilistic Error of the 6 In-Sensors: ***/ const int IN_SENSORS_WEIGHT_ERR[IN_SENSORS] = { /***/ 10 /** ID: 0 (1st)*/ ,/***/ /***/ 10 /** ID: 1 (2nd)*/ ,/***/ /***/ 10 /** ID: 2 (3rd)*/ ,/***/ /***/ 10 /** ID: 3 (4th)*/ ,/***/ /***/ 10 /** ID: 4 (5th)*/ ,/***/ /***/ 10 /** ID: 5 (6nd)*/ };/***/ /***/ /***/ /*** Probabilistic Right of the 6 In-Sensors: ***/ const int IN_SENSORS_WEIGHT_RIGHT[IN_SENSORS] = { /***/ 1 /** ID: 0 (1st)*/ ,/***/ /***/ 1 /** ID: 1 (2nd)*/ ,/***/ /***/ 1 /** ID: 2 (3rd)*/ ,/***/ /***/ 1 /** ID: 3 (4th)*/ ,/***/ /***/ 1 /** ID: 4 (5th)*/ ,/***/ /***/ 1 /** ID: 5 (6nd)*/ };/***/ /***/ /***/ /*** Probabilistic Error of the 5 Out-Sensors: ***/ const int OUT_SENSORS_WEIGHT_ERR[OUT_SENSORS] = { /***/ 1 /** ID: 0 (1st)*/ ,/***/ /***/ 1 /** ID: 1 (2nd)*/ ,/***/ /***/ 1 /** ID: 2 (3rd)*/ ,/***/ /***/ 1 /** ID: 3 (4th)*/ ,/***/ /***/ 1 /** ID: 4 (5th)*/ };/***/ /***/ /***/ /*** Probabilistic Right of the 5 Out-Sensors: ***/ const int OUT_SENSORS_WEIGHT_RIGHT[OUT_SENSORS] = { /***/ 3 /** ID: 0 (1st)*/ ,/***/ /***/ 3 /** ID: 1 (2nd)*/ ,/***/ /***/ 3 /** ID: 2 (3rd)*/ ,/***/ /***/ 3 /** ID: 3 (4th)*/ ,/***/ /***/ 3 /** ID: 4 (5th)*/ };/***/ /**********************************************************************************/
/** * System definition */
const SlotId POS_IN_SENSORS_IN_ORDER[STATIONS] = {POS_IN_SENSORS[0], POS_IN_SENSORS[1], POS_IN_SENSORS[3], POS_IN_SENSORS[2], POS_IN_SENSORS[4], POS_IN_SENSORS[5]}; const OutSensorId OUT_SENSORS_ID_IN_ORDER[STATIONS] = {1, 2, 4, 3, 4, 0}; const StationId IN_SENSORS_STATION[IN_SENSORS] = {0, 1, 3, 2, 4, 5};
initializer = Initializer(SPEED, DISKS); conveyorBelt = ConveyorBelt(); station(const StationId id) = Station(id, POS_STATIONS[id], STATIONS_ELABORATION_TIME[id], POS_IN_SENSORS_IN_ORDER[id], OUT_SENSORS_ID_IN_ORDER[id], STD_DEV_STATIONS[id]); inSensor(const InSensorId id) = InSensor(id, IN_SENSORS_STATION[id], IN_SENSORS_WEIGHT_RIGHT[id], IN_SENSORS_WEIGHT_ERR[id]); outSensor(const OutSensorId id) = OutSensor(id, POS_OUT_SENSORS[id], OUT_SENSORS_WEIGHT_RIGHT[id], OUT_SENSORS_WEIGHT_ERR[id]); // flowController = FlowController_0(POS_OUT_SENSORS[2], POS_OUT_SENSORS[3]); flowController = FlowController_3();
system initializer, conveyorBelt, station, inSensor, outSensor, flowController;
